				function init(evt) {
					if ( window.svgDocument == null ) {
						svgDoc = evt.target.ownerDocument;

						for(var i in muni) {
							//console.log(muni[i].codigo, muni[i].nome);
							var municipio = svgDoc.getElementById("mun_" + muni[i].codigo);
							var txtNumero = svgDoc.getElementById("num_" + muni[i].codigo);

							if(municipio) {
								municipio.setAttributeNS(null, "nome", muni[i].nome);
								municipio.setAttributeNS(null, "valor", muni[i].valor);

								municipio.addEventListener("mouseenter", function(e) {
									var texto = e.target.getAttributeNS(null, "nome");
									DisplayName(texto);
								}, true);

								municipio.addEventListener("onmouseout", function(e) {
								}, true);
							}

							if(txtNumero) {
								txtNumero.firstChild.data = muni[i].valor;
							}
						}
					}
				};

				function DisplayName(txt) {
					svgDoc.getElementById('path_name').firstChild.data = txt;
				};



